    private void btSalvarActionPerformed(java.awt.event.ActionEvent evt) {                                         
       UsuarioDAO listausuarios = new UsuarioDAO();
       Usuario cusuario = new Usuario();
       ConsultasUsuarios consusu = new ConsultasUsuarios();
       cusuario.setNome(txtNome.getText());
       cusuario.setSenha(txtSenha.getText());
       cusuario.setId_usuario(Integer.parseInt(campoID.getText()));
       listausuarios.adicionarUsuario(cusuario);
       listausuarios.alteraUsuarioBD(cusuario);
       consusu.preencheTabela("select * from usuarios order by usu_nome");
       dispose();
    }                                        

